# [ doc = r" Value read from the register" ] pub struct R { bits : u32 } impl super :: DTEMPALARM { # [ doc = r" Reads the contents of the register" ] # [ inline ] pub fn read ( & self ) -> R { R { bits : self . register . get ( ) } } } # [ doc = "Possible values of the field `UNDERFL`" ] # [ derive ( Clone , Copy , Debug , PartialEq ) ] pub enum UNDERFLR { # [ doc = "No temperature underflow was detected" ] VALUE1 , # [ doc = "A temperature underflow was detected" ] VALUE2 , } impl UNDERFLR { # [ doc = r" Returns `true` if the bit is clear (0)" ] # [ inline ] pub fn bit_is_clear ( & self ) -> bool { ! self . bit ( ) } # [ doc = r" Returns `true` if the bit is set (1)" ] # [ inline ] pub fn bit_is_set ( & self ) -> bool { self . bit ( ) } # [ doc = r" Value of the field as raw bits" ] # [ inline ] pub fn bit ( & self ) -> bool { match * self { UNDERFLR :: VALUE1 => false , UNDERFLR :: VALUE2 => true , } } # [ allow ( missing_docs ) ] # [ doc ( hidden ) ] # [ inline ] pub fn _from ( value : bool ) -> UNDERFLR { match value { false => UNDERFLR :: VALUE1 , true => UNDERFLR :: VALUE2 , } } # [ doc = "Checks if the value of the field is `VALUE1`" ] # [ inline ] pub fn is_value1 ( & self ) -> bool { * self == UNDERFLR :: VALUE1 } # [ doc = "Checks if the value of the field is `VALUE2`" ] # [ inline ] pub fn is_value2 ( & self ) -> bool { * self == UNDERFLR :: VALUE2 } } # [ doc = "Possible values of the field `OVERFL`" ] # [ derive ( Clone , Copy , Debug , PartialEq ) ] pub enum OVERFLR { # [ doc = "No temperature overflow was detected" ] VALUE1 , # [ doc = "A temperature overflow was detected" ] VALUE2 , } impl OVERFLR { # [ doc = r" Returns `true` if the bit is clear (0)" ] # [ inline ] pub fn bit_is_clear ( & self ) -> bool { ! self . bit ( ) } # [ doc = r" Returns `true` if the bit is set (1)" ] # [ inline ] pub fn bit_is_set ( & self ) -> bool { self . bit ( ) } # [ doc = r" Value of the field as raw bits" ] # [ inline ] pub fn bit ( & self ) -> bool { match * self { OVERFLR :: VALUE1 => false , OVERFLR :: VALUE2 => true , } } # [ allow ( missing_docs ) ] # [ doc ( hidden ) ] # [ inline ] pub fn _from ( value : bool ) -> OVERFLR { match value { false => OVERFLR :: VALUE1 , true => OVERFLR :: VALUE2 , } } # [ doc = "Checks if the value of the field is `VALUE1`" ] # [ inline ] pub fn is_value1 ( & self ) -> bool { * self == OVERFLR :: VALUE1 } # [ doc = "Checks if the value of the field is `VALUE2`" ] # [ inline ] pub fn is_value2 ( & self ) -> bool { * self == OVERFLR :: VALUE2 } } impl R { # [ doc = r" Value of the register as raw bits" ] # [ inline ] pub fn bits ( & self ) -> u32 { self . bits } # [ doc = "Bit 0 - Lower Limit Underflow" ] # [ inline ] pub fn underfl ( & self ) -> UNDERFLR { UNDERFLR :: _from ( { const MASK : bool = true ; const OFFSET : u8 = 0 ; ( ( self . bits >> OFFSET ) & MASK as u32 ) != 0 } ) } # [ doc = "Bit 16 - Upper Limit Overflow" ] # [ inline ] pub fn overfl ( & self ) -> OVERFLR { OVERFLR :: _from ( { const MASK : bool = true ; const OFFSET : u8 = 16 ; ( ( self . bits >> OFFSET ) & MASK as u32 ) != 0 } ) } }